import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import AuthModal from "../Modals/AuthModal";

const Header = () => {
  const navigate = useNavigate();
  const [isAuthModalOpened, setIsAuthModalOpened] = useState(false);
  const [contactHover, setContactHover] = useState(false); // ðŸ‘ˆ Hover state

  return (
    <header style={{ backgroundColor: "#38cfb8" }}>
      <nav>
        <div className="nav__header">
          <div className="nav__logo">
            <Link to="#">
              <img src="assets/logo.png" alt="logo" />
              SkillShare
            </Link>
          </div>
          <div className="nav__menu__btn" id="menu-btn">
            <span>
              <i className="ri-menu-line"></i>
            </span>
          </div>
        </div>

        <ul className="nav__links" id="nav-links">
          <li className="link">
            <Link
              to="/"
              style={{
                color: contactHover ? "whirete" : "black",
                transition: "color 0.3s ease",
              }}
              onMouseEnter={() => setContactHover(true)}
              onMouseLeave={() => setContactHover(false)}
            >
              Contact Us
            </Link>
          </li>
          <li className="link">
            <Link to="#browse-skills">Browse Skills</Link>
          </li>
          <li className="link">
            <Link
              to="/community"
              onClick={() => {
                if (!localStorage.getItem("userId")) {
                  setIsAuthModalOpened(true);
                }
              }}
            >
              Share Skills
            </Link>
          </li>
          <li className="link">
            <button
              onClick={() => {
                if (localStorage.getItem("userId")) {
                  navigate("/community");
                } else {
                  setIsAuthModalOpened(true);
                }
              }}
              className="btn"
            >
              Join the Community
            </button>
          </li>
        </ul>
      </nav>

      <div className="section__container header__container" id="home">
        <div>
          <img src="assets/Layer2.png" alt="header" />
        </div>
        <div className="header__content">
          <h4>Unlock Your Potential</h4>
          <h1 className="section__header">Learn, Grow, Succeed!</h1>
          <p style={{ color: "black", fontWeight: "bold" }}>
            Discover and develop a diverse array of skills, from programming and graphic design to baking,
            woodworking, digital marketing, and beyond. Sign up now to begin learning from experts or share
            your own knowledge with a vibrant community of learners.
          </p>
          <div className="header__btn">
            <button
              onClick={() => {
                if (localStorage.getItem("userId")) {
                  navigate("/community");
                } else {
                  setIsAuthModalOpened(true);
                }
              }}
              className="btn"
            >
              Start Your Skill-Building Adventure
            </button>
          </div>
        </div>
      </div>

      <AuthModal
        onClose={() => {
          setIsAuthModalOpened(false);
        }}
        isOpen={isAuthModalOpened}
      />
    </header>
  );
};

export default Header;
